 Request = (method , obj) => {

     const xhr = new XMLHttpRequest();
     xhr.open(method , 'http://localhost:3004/users', false);
     xhr.setRequestHeader('Content-Type', 'application/json');
     xhr.send(JSON.stringify(obj));
     let response

     switch(xhr.status){

         case 200 :   response = JSON.parse(xhr.responseText).id;
             break;
         case 201 :   response = JSON.parse(xhr.responseText).id;

             break;
         default :   console.log( xhr.status + ': ' + xhr.statusText );

     }

   return response
}

export default Request